function renderCart(){const list=document.getElementById('cart-list');const cart=JSON.parse(localStorage.getItem('lore_cart')||'[]');list.innerHTML='';if(cart.length===0){list.innerHTML='<p class="muted">Seu carrinho está vazio.</p>';return}cart.forEach(i=>{const el=document.createElement('div');el.className='card';el.innerHTML=`<strong>${i.name}</strong><div class='muted'>R$ ${Number(i.price).toFixed(2)} • qty: ${i.qty}</div>`;list.appendChild(el)});const total=cart.reduce((s,i)=>s + i.price * i.qty,0);document.getElementById('cart-total').innerText='R$ '+total.toFixed(2)}document.addEventListener('DOMContentLoaded',()=>{if(document.getElementById('cart-list'))renderCart();});